     */
    fun getYearInChinese(): String {
        val y = year.toString() + ""
        val s = StringBuilder()
        var i = 0
        val j = y.length
        while (i < j) {
            s.append(LunarUtil.NUMBER[y[i] - '0'])
            i++
        }
        return s.toString()
    }

