    /**
     * @return 获取节令
     */
    fun getJie(): String {
        for (jie in LunarUtil.JIE) {
            jieQi[jie]?.apply {
                if (getYear() == solar.getYear() && getMonth() == solar.getMonth() && getDay() == solar.getDay()) {
                    return jie
                }
            }
        }
        // 追加的节令：大雪
        jieQi[JIE_QI_PREPEND]?.apply {
            if (getYear() == solar.getYear() && getMonth() == solar.getMonth() && getDay() == solar.getDay()) {
                return JIE_QI_LAST
            }
        }
        return ""
    }

    /**
     * @return 获取气令
     */
    fun getQi(): String {
        for (qi in LunarUtil.QI) {
            jieQi[qi]?.apply {
                if (getYear() == solar.getYear() && getMonth() == solar.getMonth() && getDay() == solar.getDay()) {
                    return qi
                }
            }
        }
        // 追加的气令：冬至
        jieQi[JIE_QI_APPEND]?.apply {
            if (getYear() == solar.getYear() && getMonth() == solar.getMonth() && getDay() == solar.getDay()) {
                return JIE_QI_FIRST
            }
        }
        return ""
    }

