    /**
     * 转换为阳历日期
     *
     * @return 阳历日期
     */
    private fun toSolar(): Solar {
        val c = Calendar.getInstance()
        c.set(
            SolarUtil.BASE_YEAR,
            SolarUtil.BASE_MONTH - 1,
            SolarUtil.BASE_DAY,
            hour,
            minute,
            second
        )
        c.add(Calendar.DATE, dayOffset)
        return Solar(c)
    }

