    fun getDayChongShengXiao(): String {
        val chong = getDayChong()
        var i = 0
        val j = LunarUtil.ZHI.size
        while (i < j) {
            if (LunarUtil.ZHI[i] == chong) {
                return LunarUtil.SHENGXIAO[i]
            }
            i++
        }
        return ""
    }

