    /**
     * 获取往后推几天的农历日期，如果要往前推，则天数用负数
     * @param days 天数
     * @return 农历日期
     */
    fun next(days: Int): Lunar {
        var y = year
        var m = month
        var d = day
        if (days > 0) {
            var daysInMonth = getDaysOfMonth(y, m)
            var rest = day + days
            while (daysInMonth < rest) {
                if (m > 0) {
                    if (getLeapMonth(y) != m) {
                        m++
                    } else {
                        m = -m
                    }
                } else {
                    m = 1 - m
                }
                if (13 == m) {
                    y++
                    m = 1
                }
                rest -= daysInMonth
                daysInMonth = getDaysOfMonth(y, m)
            }
            d = rest
        } else if (days < 0) {
            var daysInMonth = day
            var rest = -days
            while (daysInMonth <= rest) {
                if (m > 0) {
                    m--
                    if (0 == m) {
                        y--
                        m = if (getLeapMonth(y) != 12) 12 else -12
                    }
                } else {
                    m = -m
                }
                rest -= daysInMonth
                daysInMonth = getDaysOfMonth(y, m)
            }
            d = daysInMonth - rest
        }
        return Lunar(y, m, d, hour, minute, second)
    }

