package com.tiamosu.lunar.utils

import com.tiamosu.lunar.Holiday

/**
 * 描述：法定节假日工具（自2011年1月1日起）
 *
 * @author tiamosu
 * @date 2020/6/1.
 */
object HolidayUtil {
    /** 数据段长度  */
    private const val SIZE = 18

    /** 0  */
    private const val ZERO = '0'

    /** 节假日名称（元旦0，春节1，清明2，劳动3，端午4，中秋5，国庆6，国庆中秋7，抗战胜利日8）  */
    private val NAMES =
        arrayOf("元旦节", "春节", "清明节", "劳动节", "端午节", "中秋节", "国庆节", "国庆中秋", "抗战胜利日")

    /** 节假日数据，日期YYYYMMDD+名称下标+是否调休+对应节日YYYYMMDD  */
    private const val DATA =
        ""

    private fun padding(n: Int): String {
        return (if (n < 10) "0" else "") + n
    }

    private fun buildHolidayForward(s: String): Holiday {
        val day = s.substring(0, 8)
        val name = NAMES[s[8] - ZERO]
        val work = s[9] == ZERO
        val target = s.substring(10, SIZE)
        return Holiday(day, name, work, target)
    }

    private fun buildHolidayBackward(s: String?): Holiday {
        val size = s!!.length
        val day = s.substring(size - 18, size - 10)
        val name = NAMES[s[size - 10] - ZERO]
        val work = s[size - 9] == ZERO
        val target = s.substring(size - 8)
        return Holiday(day, name, work, target)
    }

    private fun findForward(key: String): String? {
        val start = DATA.indexOf(key)
        if (start < 0) {
            return null
        }
        var right = DATA.substring(start)
        val n = right.length % SIZE
        if (n > 0) {
            right = right.substring(n)
        }
        while (!right.startsWith(key) && right.length >= SIZE) {
            right = right.substring(SIZE)
        }
        return right
    }

    private fun findBackward(key: String): String? {
        val start = DATA.lastIndexOf(key)
        if (start < 0) {
            return null
        }
        var left = DATA.substring(0, start + key.length)
        var size = left.length
        val n = size % SIZE
        if (n > 0) {
            left = left.substring(0, size - n)
        }
        size = left.length
        while (!left.endsWith(key) && size >= SIZE) {
            left = left.substring(0, size - SIZE)
            size = left.length
        }
        return left
    }

    private fun findHolidaysForward(key: String): List<Holiday> {
        val l: MutableList<Holiday> = ArrayList()
        var s = findForward(key) ?: return l
        while (s.startsWith(key)) {
            l.add(buildHolidayForward(s))
            s = s.substring(SIZE)
        }
        return l
    }

    private fun findHolidaysBackward(key: String): List<Holiday> {
        val l: MutableList<Holiday> = ArrayList()
        var s = findBackward(key) ?: return l
        while (s.endsWith(key)) {
            l.add(buildHolidayBackward(s))
            s = s.substring(0, s.length - SIZE)
        }
        l.reverse()
        return l
    }

    /**
     * 获取指定年月日的节假日信息，如果不存在，返回null
     * @param year 年
     * @param month 月，1-12
     * @param day 日，1-31
     * @return 节假日
     */
    fun getHoliday(year: Int, month: Int, day: Int): Holiday? {
        val l =
            findHolidaysForward(year.toString() + padding(month) + padding(day))
        return if (l.isEmpty()) null else l[0]
    }

    /**
     * 获取指定年月日的节假日信息，如果不存在，返回null
     * @param ymd 年月日
     * @return 节假日
     */
    fun getHoliday(ymd: String): Holiday? {
        val l = findHolidaysForward(ymd.replace("-", ""))
        return if (l.isEmpty()) null else l[0]
    }

    /**
     * 获取指定年月的节假日列表
     * @param year 年
     * @param month 月，1-12
     * @return 节假日列表
     */
    fun getHolidays(year: Int, month: Int): List<Holiday> {
        return findHolidaysForward(year.toString() + padding(month))
    }

    /**
     * 获取指定年的节假日列表
     * @param year 年
     * @return 节假日列表
     */
    fun getHolidays(year: Int): List<Holiday> {
        return findHolidaysForward(year.toString() + "")
    }

    /**
     * 获取指定年月日的节假日列表
     * @param ymd 年、年月、年月日
     * @return 节假日列表
     */
    fun getHolidays(ymd: String): List<Holiday> {
        return findHolidaysForward(ymd.replace("-", ""))
    }

    /**
     * 获取指定节日（仅节日当天）的相关节假日列表
     * @param ymd 年月日
     * @return 节假日列表
     */
    fun getHolidaysByTarget(ymd: String): List<Holiday> {
        return findHolidaysBackward(ymd.replace("-", ""))
    }

    /**
     * 获取指定节日（仅节日当天）的相关节假日列表
     * @param year 年
     * @param month 月
     * @param day 日
     * @return 节假日列表
     */
    fun getHolidaysByTarget(
        year: Int,
        month: Int,
        day: Int
    ): List<Holiday> {
        return findHolidaysBackward(year.toString() + padding(month) + padding(day))
    }
}